        private void Init_Resolution()
        {
            if (File.Exists(_chemin_Resolution))
            {
                using (StreamReader fichier = new StreamReader(_chemin_Resolution, Encoding.UTF8))
                {
                    String ligne;
                    int i = 0;
                    while ((ligne = fichier.ReadLine()) != null)
                    {
                        if (!string.IsNullOrWhiteSpace(ligne))
                        {
                            string[] champs = ligne.Split(';');
                            _Resolution[i] = Convert.ToInt32(champs[0]);
                            i++;
                        }
                    }
                }
            }
            else
            {
                Resolution();
            }
        }
        private void Resolution()
        {
            this.MinHeight = 600;
            this.MinWidth = 800;
            this.WindowState = WindowState.Normal;
            this.Show();
            _Resolution[0] = Convert.ToInt32(this.ActualWidth);
            _Resolution[1] = Convert.ToInt32(this.ActualHeight);
            Classe_Resolution.Sauvegarder_Resolution_CSV(_Resolution);
        }
        private void Window_SizeChanged(object sender, SizeChangedEventArgs e)
        {
            _Resolution[0] = Convert.ToInt32(this.ActualWidth);
            _Resolution[1] = Convert.ToInt32(this.ActualHeight);
            Classe_Resolution.Sauvegarder_Resolution_CSV(_Resolution);
            Taille();
            Deroulement();
        }
        private void Taille()
        {
            if (_Resolution[0] != 0)
            {
                Rapport_Taille_X = _Resolution[0] / 80;
                Rapport_Taille_Y = _Resolution[1] / 60;
            }
            else
            {
                Resolution();
            }
        }
        public void Rapport_de_Taille()
        {
            Multiple = (Rapport_Taille_X + Rapport_Taille_Y) / 4;
            Taille_texte = 1.4;
            Largeur_saisie = 1.8;
            Hauteur_saisie = 1.8;/// 1.6 d origine 
        }


