        private void Button_Map_Ok_Click(object sender, RoutedEventArgs e)
        {
            if (format == 1000 || format == 2000)
            {
                if (TextBox_Map_A.Text != "")
                {

                    int dé_Dizaine;
                    int dé_Unité;
                    dé_Dizaine = (Convert.ToInt16(TextBox_Map_A.Text) / 10);
                    dé_Unité = (Convert.ToInt16(TextBox_Map_A.Text) - (dé_Dizaine * 10));
                    dé_Dizaine = D3(dé_Dizaine);
                    dé_Unité = D3(dé_Unité);
                    map = (dé_Dizaine * 10) + dé_Unité;
                    if (map == 11 || map == 12 || map == 13 || map == 21 || map == 22 || map == 23 || map == 31 || map == 32 || map == 33)
                    {
                        format = format + map;
                        Init_affichage();
                        Pages = 6;
                        Deroulement();
                    }
                    else
                    {
                        MessageBox.Show("Saisie incorrect , veillez saisir une mission !", "erreur", MessageBoxButton.OK, MessageBoxImage.Error);
                        map = 0;
                        TextBox_Map_A.Text = null;
                        StackPanel_MAPS.Visibility = Visibility.Visible;
                    }
                }
                else
                {
                    MessageBox.Show("Saisie incorrect , veillez saisir une mission !", "erreur", MessageBoxButton.OK, MessageBoxImage.Error);
                    map = 0;
                    TextBlock_Map.Text = null;
                    TextBox_Map_A.Text = null;
                    StackPanel_MAPS.Visibility = Visibility.Visible;
                }
            }
            if (format == 500 || format == 3000)
            {
                if (TextBox_Map_A.Text != "")
                {
                    map = Convert.ToInt16(D3(Convert.ToInt16(TextBox_Map_A.Text)));
                    if (map == 1 || map == 2 || map == 3)
                    {
                        format = format + map;
                        Init_affichage();
                        Pages = 6;
                        Deroulement();
                    }
                    else
                    {
                        MessageBox.Show("Saisie incorrect , veillez saisir une mission !", "erreur", MessageBoxButton.OK, MessageBoxImage.Error);
                        map = 0;
                        TextBox_Map_A.Text = null;
                        StackPanel_MAPS.Visibility = Visibility.Visible;
                    }
                }
                else
                {
                    MessageBox.Show("Saisie incorrect , veillez saisir une mission !", "erreur", MessageBoxButton.OK, MessageBoxImage.Error);
                    map = 0;
                    TextBlock_Map.Text = null;
                    TextBox_Map_A.Text = null;
                    StackPanel_MAPS.Visibility = Visibility.Visible;
                }
            }
            FormatBis();

        }
