        public int D3(int dé)
        {
            if (dé == 5 || dé == 6)
            {
                dé = 3;
                return dé;
            }
            else if (dé == 3 || dé == 4)
            {
                dé = 2;
                return dé;
            }
            else if (dé == 1 || dé == 2)
            {
                dé = 1;
                return dé;
            }
            return dé;
        }


